import React, { useState, useEffect, useRef } from 'react';
import StartScreen from './components/StartScreen';
import GameScene from './components/GameScene';
import EndScreen from './components/EndScreen';
import './App.css';

function App() {
  const [gameState, setGameState] = useState('start'); // start, playing, end
  const [selectedDifficulty, setSelectedDifficulty] = useState(null);
  const [sessionId, setSessionId] = useState(null);
  const [sessionData, setSessionData] = useState(null);
  const [quizResponses, setQuizResponses] = useState([]);
  const [attemptedQuizzes, setAttemptedQuizzes] = useState(new Set());
  const sessionDataRef = useRef(null);

  // ゲーム開始時
  const handleDifficultySelect = async (difficulty) => {
    const newSessionId = `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

    try {
      // バックエンドにセッション開始を通知
      const response = await fetch('http://localhost:5001/api/data/session/start', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          difficulty,
          timestamp: Date.now()
        })
      });

      const data = await response.json();

      setSelectedDifficulty(difficulty);
      setSessionId(data.sessionId);
      setQuizResponses([]); // クイズ回答をリセット
      setGameState('playing');

      // セッションデータをメモリに保存
      sessionDataRef.current = {
        id: data.sessionId,
        difficulty,
        startTime: Date.now(),
        sensorData: [],
        quizResponses: [],
        endTime: null
      };
    } catch (error) {
      console.error('Failed to start session:', error);
    }
  };

  // クイズ回答データを記録
  const handleQuizResponse = (quizId, isCorrect) => {
    const response = {
      quizId,
      isCorrect,
      timestamp: Date.now()
    };

    // App.jsx の状態に記録
    setQuizResponses(prev => [...prev, response]);

    // sessionDataRef にも記録（バックアップ用）
    if (sessionDataRef.current) {
      sessionDataRef.current.quizResponses.push(response);
    }

    console.log(`Quiz response recorded: ${quizId} - ${isCorrect ? 'correct' : 'incorrect'}`);
  };

  // ゲーム終了時
  const handleGameEnd = async (reason) => {
    if (!sessionDataRef.current) return;

    const endTime = Date.now();
    sessionDataRef.current.endTime = endTime;

    console.log('=== Game End - Final Session Data ===');
    console.log('Quiz Responses:', sessionDataRef.current.quizResponses);

    try {
      // バックエンドにセッション終了を通知
      await fetch('http://localhost:5001/api/data/session/end', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          sessionId,
          timestamp: endTime
        })
      });

      // セッションデータを保存して終了画面へ
      setSessionData(sessionDataRef.current);
      setGameState('end');
    } catch (error) {
      console.error('Failed to end session:', error);
      setGameState('end');
    }
  };

  const handleTimeUp = () => {
    console.log('時間終了');
    handleGameEnd({ reason: 'timeup' });
  };

  // スタート画面に戻る
  const handleRestart = () => {
    setGameState('start');
    setSelectedDifficulty(null);
    setSessionId(null);
    setSessionData(null);
    sessionDataRef.current = null;
  };

  return (
    <div className="App">
      {gameState === 'start' && <StartScreen onDifficultySelect={handleDifficultySelect} />}

      {gameState === 'playing' && (
        <GameScene
          difficulty={selectedDifficulty}
          sessionId={sessionId}
          onGameEnd={handleGameEnd}
          onTimeUp={handleTimeUp}
          onQuizResponse={handleQuizResponse}
          quizResponses={quizResponses}
        />
      )}

      {gameState === 'end' && sessionData && (
        <EndScreen sessionData={sessionData} difficulty={selectedDifficulty} onRestart={handleRestart} />
      )}
    </div>
  );
}

export default App;
