import React, { useEffect, useRef, useState } from 'react';
import { useGameTimer } from '../hooks/useGameTimer';
import DataCollector from '../utils/dataCollector';
import QuizBooth from './QuizBooth';
import './GameScene.css';

const GameScene = ({ difficulty, sessionId, onGameEnd, onTimeUp, onQuizResponse, onQuizAttempt, quizResponses, attemptedQuizzes }) => {
  const containerRef = useRef(null);
  const canvasRef = useRef(null);
  const { formattedTime, remainingTime } = useGameTimer(difficulty, onTimeUp);
  const dataCollectorRef = useRef(null);
  const quizBoothsRef = useRef([]);
  const activeQuizRef = useRef(null);

  const [quizzes, setQuizzes] = useState([]);
  const [solvedQuizzes, setSolvedQuizzes] = useState(new Set());
  const [activeQuizId, setActiveQuizId] = useState(null);
  const [isInitialized, setIsInitialized] = useState(false);

  // Canvas ÂàùÊúüÂåñ
  useEffect(() => {
    if (!containerRef.current) return;

    // Êó¢Â≠ò„ÅÆ canvas „Åå„ÅÇ„Çå„Å∞ÂâäÈô§
    const existingCanvas = containerRef.current.querySelector('canvas');
    if (existingCanvas) {
      existingCanvas.remove();
    }

    const width = window.innerWidth;
    const height = window.innerHeight;

    const canvas = document.createElement('canvas');
    canvas.width = width;
    canvas.height = height;
    canvas.style.display = 'block';
    canvas.style.width = '100%';
    canvas.style.height = '100%';
    canvas.style.position = 'absolute';
    canvas.style.top = '0';
    canvas.style.left = '0';

    containerRef.current.appendChild(canvas);
    canvasRef.current = canvas;

    // „ÇØ„É™„ÉÉ„ÇØ„ÇíÁ¢∫ÂÆü„Å´Âèó„ÅëÂèñ„Çã„Åü„ÇÅ
    canvas.style.cursor = 'pointer';

    console.log('üé® Canvas initialized:', { width, height });
    setIsInitialized(true);
  }, []);

  // „ÇØ„Ç§„Ç∫„Éá„Éº„ÇøÂèñÂæó
  useEffect(() => {
    const fetchQuizzes = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/quiz/difficulty/${difficulty}`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        console.log(`Loaded ${data.quizzes?.length || 0} quizzes for difficulty: ${difficulty}`);
        setQuizzes(data.quizzes || []);
      } catch (error) {
        console.error('Failed to fetch quizzes:', error);
        setQuizzes([]);
      }
    };

    if (difficulty) {
      fetchQuizzes();
    }
  }, [difficulty]);

  // „Éè„Éº„ÉàÂûã„ÇíÊèèÁîª„Åô„Çã„Éò„É´„Éë„ÉºÈñ¢Êï∞
  const drawHeart = (ctx, x, y, size, fillColor, strokeColor, quizId) => {
    ctx.fillStyle = fillColor;
    ctx.strokeStyle = strokeColor;
    ctx.lineWidth = 3;

    // „Éè„Éº„ÉàÂûã„ÇíÊèèÁîª
    const heartSize = size / 2;
    const topY = y - heartSize / 3;
    const bottomY = y + heartSize * 0.6;

    ctx.beginPath();
    // Â∑¶„ÅÆ‰∏∏„ÅÑÈÉ®ÂàÜ
    ctx.bezierCurveTo(
      x - heartSize * 0.7, topY - heartSize * 0.3,
      x - heartSize * 0.7, topY + heartSize * 0.2,
      x - heartSize * 0.2, topY + heartSize * 0.4
    );
    // ‰∏ã„ÅÆÂ∞ñ„Å£„ÅüÈÉ®ÂàÜ
    ctx.bezierCurveTo(
      x, bottomY,
      x, bottomY,
      x + heartSize * 0.2, topY + heartSize * 0.4
    );
    // Âè≥„ÅÆ‰∏∏„ÅÑÈÉ®ÂàÜ
    ctx.bezierCurveTo(
      x + heartSize * 0.7, topY + heartSize * 0.2,
      x + heartSize * 0.7, topY - heartSize * 0.3,
      x, topY - heartSize * 0.1
    );
    ctx.closePath();
    ctx.fill();
    ctx.stroke();

    // „ÇØ„Ç§„Ç∫Áï™Âè∑„ÇíÊèèÁîªÔºà„Éè„Éº„Éà„ÅÆ‰∏≠Â§Æ„Å´Ôºâ
    ctx.fillStyle = '#ffffff';
    ctx.font = 'bold 20px Arial';
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    ctx.fillText(quizId, x, y);
  };

  // Canvas „É¨„É≥„ÉÄ„É™„É≥„Ç∞
  useEffect(() => {
    if (!canvasRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // ËÉåÊôØËâ≤ - ÊäπËå∂„Çπ„É†„Éº„Ç∏„Éº„ÅÆ„Çà„ÅÜ„Å™„Éë„Çπ„ÉÜ„É´„Ç∞„É™„Éº„É≥
    ctx.fillStyle = '#d4f1d8';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // „Ç∞„É™„ÉÉ„ÉâÊèèÁîªÔºàÊúÄÈÅ©ÂåñÁâà - 80px„Åî„Å®„ÄÅÊ∑°„ÅÑËâ≤Ôºâ
    ctx.strokeStyle = 'rgba(139, 195, 156, 0.3)';
    ctx.lineWidth = 0.8;
    const gridSize = 80;
    for (let i = 0; i < canvas.width; i += gridSize) {
      ctx.beginPath();
      ctx.moveTo(i, 0);
      ctx.lineTo(i, canvas.height);
      ctx.stroke();
    }
    for (let i = 0; i < canvas.height; i += gridSize) {
      ctx.beginPath();
      ctx.moveTo(0, i);
      ctx.lineTo(canvas.width, i);
      ctx.stroke();
    }

    // „ÇØ„Ç§„Ç∫„Éñ„Éº„Çπ„ÇíÊèèÁîªÔºàÂãïÁâ©„ÅÆÈ°î„Åß„É©„É≥„ÉÄ„É†ÈÖçÁΩÆ„ÄÅÈáç„Å™„Çâ„Å™„ÅÑÊù°‰ª∂Ôºâ
    if (quizzes.length > 0) {
      const boothSize = 80;
      const minDistance = boothSize + 40; // ÂãïÁâ©ÂêåÂ£´„ÅÆÊúÄÂ∞èË∑ùÈõ¢Ôºà„Éñ„Éº„Çπ„Çµ„Ç§„Ç∫ + 40px‰ΩôË£ïÔºâ
      const padding = boothSize / 2 + 10; // ÁîªÈù¢Á´Ø„Åã„Çâ„ÅÆ‰ΩôË£ï

      // Êó¢„Å´„Éó„É¨„Éº„Çπ„Åï„Çå„Åü„Éñ„Éº„Çπ„ÅÆ‰ΩçÁΩÆ„Çí‰øùÊåÅ
      const placedBooths = [];

      quizBoothsRef.current = quizzes.map((quiz, index) => {
        let x, y;
        let isValidPosition = false;
        let attempts = 0;
        const maxAttempts = 100;

        // „É©„É≥„ÉÄ„É†„Å™‰ΩçÁΩÆ„ÇíÊé¢„ÅôÔºàÊó¢Â≠ò„ÅÆ„Éñ„Éº„Çπ„Å®Èáç„Å™„Çâ„Å™„ÅÑ„Åì„Å®„ÇíÁ¢∫Ë™çÔºâ
        while (!isValidPosition && attempts < maxAttempts) {
          x = padding + Math.random() * (canvas.width - padding * 2);
          y = padding + Math.random() * (canvas.height - padding * 2);

          // Êó¢„Å´„Éó„É¨„Éº„Çπ„Åï„Çå„Åü„Åô„Åπ„Å¶„ÅÆ„Éñ„Éº„Çπ„Å®„ÅÆË∑ùÈõ¢„Çí„ÉÅ„Çß„ÉÉ„ÇØ
          isValidPosition = true;
          for (const placedBooth of placedBooths) {
            const dx = x - placedBooth.x;
            const dy = y - placedBooth.y;
            const distance = Math.sqrt(dx * dx + dy * dy);

            // ÊúÄÂ∞èË∑ùÈõ¢„Çà„ÇäËøë„ÅÑÂ†¥Âêà„ÅØÁÑ°Âäπ
            if (distance < minDistance) {
              isValidPosition = false;
              break;
            }
          }
          attempts++;
        }

        // ‰ΩçÁΩÆ„ÅåË¶ã„Å§„Åã„Çâ„Å™„ÅÑÂ†¥Âêà„ÅØ„Ç∞„É™„ÉÉ„ÉâÈÖçÁΩÆ„Å´„Éï„Ç©„Éº„É´„Éê„ÉÉ„ÇØ
        if (!isValidPosition) {
          const gridCols = Math.ceil(Math.sqrt(quizzes.length));
          const gridRows = Math.ceil(quizzes.length / gridCols);
          const cellWidth = canvas.width / gridCols;
          const cellHeight = canvas.height / gridRows;

          const col = index % gridCols;
          const row = Math.floor(index / gridCols);

          // „Ç∞„É™„ÉÉ„Éâ„Çª„É´„ÅÆ‰∏≠Â§Æ„Å´„É©„É≥„ÉÄ„É†„Å™„Ç™„Éï„Çª„ÉÉ„Éà„ÇíÂä†„Åà„Çã
          x = (col + 0.5) * cellWidth + (Math.random() - 0.5) * (cellWidth * 0.3);
          y = (row + 0.5) * cellHeight + (Math.random() - 0.5) * (cellHeight * 0.3);

          // ÁîªÈù¢Á´Ø„ÇíË∂Ö„Åà„Å™„ÅÑ„Çà„ÅÜ„Å´„ÇØ„É™„ÉÉ„Éó
          x = Math.max(padding, Math.min(canvas.width - padding, x));
          y = Math.max(padding, Math.min(canvas.height - padding, y));
        }

        // „Éó„É¨„Éº„Çπ„Åó„Åü„Éñ„Éº„Çπ„ÇíË®òÈå≤
        placedBooths.push({ x, y });

        // „ÇØ„Ç§„Ç∫„ÅÆÁä∂ÊÖã„ÇíÁ¢∫Ë™ç
        const quizResponse = quizResponses?.find(r => r.quizId === quiz.id);
        const isAnswered = !!quizResponse;
        const isAttempted = attemptedQuizzes?.has(quiz.id);

        // ÂãïÁâ©„ÅÆËâ≤„ÇíÊ±∫ÂÆö
        let fillColor = '#FFD1DC'; // „Éá„Éï„Ç©„É´„ÉàÔºöÊ∑°„ÅÑ„Éî„É≥„ÇØÔºàÊú™Ë©¶Ë°åÔºâ
        let strokeColor = '#FF69B4';
        let animalType = 'seal';

        if (isAnswered) {
          // ÂõûÁ≠îÊ∏à„ÅøÂïèÈ°åÔºöÊ≠£Ëß£„Å™„ÇâÁ∑ë„ÄÅ‰∏çÊ≠£Ëß£„Å™„ÇâËµ§
          if (quizResponse.isCorrect) {
            fillColor = '#A8D8A8'; // Ê∑°„ÅÑÁ∑ë
            strokeColor = '#4CAF50';
          } else {
            fillColor = '#FFB3B3'; // Ê∑°„ÅÑËµ§
            strokeColor = '#f44336';
          }
        } else if (isAttempted) {
          // Ë©¶Ë°åÊ∏à„Åø„Å†„ÅåÊú™ÂõûÁ≠îÔºöÊ∑°„ÅÑÈùí
          fillColor = '#ADD8E6';
          strokeColor = '#2196F3';
        }

        // „Éè„Éº„ÉàÂûã„ÇíÊèèÁîª
        drawHeart(ctx, x, y, boothSize, fillColor, strokeColor, quiz.id);

        return { id: quiz.id, x, y, size: boothSize };
      });

      console.log('‚ù§Ô∏è Canvas rendered with', quizBoothsRef.current.length, 'cute heart icons');
    } else {
      console.log('‚è≥ Waiting for quizzes to load...');
      quizBoothsRef.current = [];
    }
  }, [quizzes, solvedQuizzes, quizResponses, attemptedQuizzes]);

  // „Éû„Ç¶„Çπ„ÇØ„É™„ÉÉ„ÇØÂá¶ÁêÜÔºà„Éà„É©„ÉÉ„ÇØ„Éë„ÉÉ„ÉâÂØæÂøúÔºâ
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const handlePointerInteraction = (event) => {
      const rect = canvas.getBoundingClientRect();
      const clickX = event.clientX - rect.left;
      const clickY = event.clientY - rect.top;

      console.log(`üñ±Ô∏è Pointer detected at canvas coordinates: (${Math.round(clickX)}, ${Math.round(clickY)})`);
      console.log(`üìç Current booth count: ${quizBoothsRef.current.length}`);

      if (quizBoothsRef.current.length === 0) {
        console.log('‚ö†Ô∏è No booths available yet');
        return;
      }

      for (const booth of quizBoothsRef.current) {
        const dx = clickX - booth.x;
        const dy = clickY - booth.y;
        const distance = Math.sqrt(dx * dx + dy * dy);

        // Check if quiz has been answered
        const isAnswered = quizResponses?.find(r => r.quizId === booth.id);
        // ÂãïÁâ©„ÅÆÈ°î„ÅÆÈ†≠ÈÉ®ÂàÜ„ÅåÁ¥Ñbooth.size / 2„ÅÆÂçäÂæÑ„Å™„ÅÆ„Åß„ÄÅ„ÇØ„É™„ÉÉ„ÇØÂà§ÂÆö„ÇÇ„Åù„Çå„Å´Âêà„Çè„Åõ„Çã
        const clickThreshold = booth.size / 2 + 10; // Â∞ë„Åó‰ΩôË£ï„ÇíÊåÅ„Åü„Åõ„Çã
        console.log(`  Booth ${booth.id}: distance=${Math.round(distance)}, threshold=${clickThreshold}, answered=${!!isAnswered}`);

        if (distance < clickThreshold && !isAnswered) {
          console.log(`‚úÖ Clicked booth: ${booth.id}`);
          // Record attempt when opening quiz
          if (onQuizAttempt) {
            onQuizAttempt(booth.id);
          }
          setActiveQuizId(booth.id);
          activeQuizRef.current = booth.id;
          event.preventDefault();
          break;
        }
      }
    };

    // Ë§áÊï∞„ÅÆ„Ç§„Éô„É≥„Éà„Çø„Ç§„Éó„Çí„É™„Çπ„É≥
    canvas.addEventListener('click', handlePointerInteraction);
    canvas.addEventListener('mousedown', handlePointerInteraction);
    canvas.addEventListener('touchstart', handlePointerInteraction);

    console.log('‚úÖ Event listeners attached to canvas');

    return () => {
      canvas.removeEventListener('click', handlePointerInteraction);
      canvas.removeEventListener('mousedown', handlePointerInteraction);
      canvas.removeEventListener('touchstart', handlePointerInteraction);
      console.log('üßπ Event listeners removed from canvas');
    };
  }, []);

  // „Éá„Éº„ÇøÂèéÈõÜÈñãÂßã
  useEffect(() => {
    if (!isInitialized || !sessionId) return;

    dataCollectorRef.current = new DataCollector(sessionId);
    dataCollectorRef.current.start();

    return () => {
      if (dataCollectorRef.current) {
        dataCollectorRef.current.stop();
      }
    };
  }, [isInitialized, sessionId]);

  const handleQuizClose = async (quizId, isCorrect) => {
    // „ÇØ„Ç§„Ç∫ÂõûÁ≠î„Éá„Éº„Çø„ÇíË®òÈå≤
    if (onQuizResponse) {
      onQuizResponse(quizId, isCorrect);
    }

    setSolvedQuizzes(prev => new Set(prev).add(quizId));
    setActiveQuizId(null);

    // „Åô„Åπ„Å¶„ÅÆ„ÇØ„Ç§„Ç∫„Å´ÂõûÁ≠î„Åó„Åü„ÅãÁ¢∫Ë™ç
    // quizResponses„ÅØÊõ¥Êñ∞‰∏≠„ÅÆÂèØËÉΩÊÄß„Åå„ÅÇ„Çã„Åü„ÇÅ„ÄÅÊâãÂãï„ÅßË®àÁÆó
    const totalAnswered = (quizResponses?.length || 0) + (isCorrect !== null ? 1 : 0);
    if (totalAnswered === quizzes.length) {
      onGameEnd({ reason: 'completed' });
    }
  };

  return (
    <div className="game-scene">
      <div ref={containerRef} className="three-container"></div>

      {/* UI „Ç™„Éº„Éê„Éº„É¨„Ç§ */}
      <div className="game-ui">
        {/* „Çø„Ç§„Éû„Éº */}
        <div className={`timer ${remainingTime < 60 ? 'warning' : ''}`}>
          <span className="label">Âà∂ÈôêÊôÇÈñì:</span>
          <span className="time">{formattedTime}</span>
        </div>

        {/* ÈÄ≤Êçó */}
        <div className="progress">
          <span className="label">ÂõûÁ≠î„Åó„ÅüÂïèÈ°å:</span>
          <span className="count">{quizResponses.length} / {quizzes.length}</span>
        </div>
      </div>

      {/* „ÇØ„Ç§„Ç∫„Éù„ÉÉ„Éó„Ç¢„ÉÉ„ÉóÔºàUI „Çà„Çä‰∏ä„Å´ÈÖçÁΩÆÔºâ */}
      {activeQuizId && (
        <QuizBooth
          quiz={quizzes.find(q => q.id === activeQuizId)}
          difficulty={difficulty}
          onClose={handleQuizClose}
          dataCollector={dataCollectorRef.current}
        />
      )}
    </div>
  );
};

export default GameScene;
